import React from "react";

export async function getServerSideProps({ query }) {
  let id = query.id;
  let fetchClip = await fetch(
    `https://api.audioboom.com/audio_clips/${id}.mp3`
  );
  let clip = (await fetchClip.json()).body.audio_clip;
  return { props: { clip } };
}

export default ({ clip }) => {
  return (
    <React.Fragment>
      <h1>{clip.id}</h1>
    </React.Fragment>
  );
};
